$(document).ready(function() {
	$.fn.fullpage({
		anchors: ['page1', 'page2', 'page3', 'page4'],
		menu: '#menu',
		afterRender: function() {
			$('.inner a').each(function() {
				var $rel = $(this).attr('rel');
				var $arr = $rel.split(',');
				$(this).animate({
					left: $arr[0] + 'px',
					top: $arr[1] + 'px'
				}, 500);
			});
		},
		afterLoad: function(anchorLink, index) {
			if (index == 1) {
				$(".menudiv").hide();
			}
			if (index == 2 || index == 3 || index == 4) {
				$(".menudiv").show();
				$('.inner').eq(index - 2).find('a').each(function() {
					var $rel = $(this).attr('rel');
					var $arr = $rel.split(',');
					$(this).animate({
						left: $arr[2] + 'px',
						top: $arr[3] + 'px'
					}, 500);
				});
				switch (index) {
					case 2:
						btnPageSecond();
						break;
					case 3:
						tooptips();
						tooptip_active();
						break;
					case 4:
						btnFourPage();
						break;
				}
			}
		},
		onLeave: function(index, direction) {
			if (index == 1) {
				$(".menudiv").hide();

			}
			if (index == 2 || index == 3 || index == 4) {
				$(".menudiv").show();
				$('.inner').eq(index - 2).find('a').each(function() {
					var $rel = $(this).attr('rel');
					var $arr = $rel.split(',');
					$(this).animate({
						left: $arr[0] + 'px',
						top: $arr[1] + 'px'
					}, 500);
				});
			}
		}
	});
	banner();
	onlineserver();
	ScreenWidth();
});

function btn_menu() {
	$(".menubanner").find("li").bind("mouseover", function() {
		$(".menubanner").find("li").removeClass("active");
		$(this).addClass("active");
	});
}

function banner() {
	var swiper = new Swiper('.swiper-container', {
		pagination: '.swiper-pagination',
		paginationClickable: true,
		spaceBetween: 30,
		centeredSlides: true,
		autoplay: 2500,
		autoplayDisableOnInteraction: false
	});
}

function onlineserver() {
	$('.onlineserver').hover(function() {
		$(".onlineserver").css('right', '0');
	}, function() {
		$(".onlineserver").css('right', '-120px');
	});

}

function tooptips() {
	jQuery(document).ready(function() {
		// Tipso for Image
		jQuery('.img-tipso').tipso({
			useTitle: false,
			background: 'rgba(255,255,255,1)'
		});
	});
}

function tooptip_active() {
	$(".servertxt").hover(function() {
		$(this).addClass("servertxt_active");

	}, function() {
		$(this).removeClass("servertxt_active");
	});

}

function btnFourPage() {
	$(".us_left").hover(function() {
		$(".us_right").hide();
		$(".kf_khd").show();
		$(".tbnwebserver").show();
	}, function() {
		$(".us_right").show();
		$(".kf_khd").hide();
		$(".tbnwebserver").hide();
	});

	$(".us_right").hover(function() {
		$(".us_left").hide();
		$(".kf_shd").show();
		$(".tbnwebserver").show();
	}, function() {
		$(".us_left").show();
		$(".kf_shd").hide();
		$(".tbnwebserver").hide();
	});
}

function btnPageSecond() {
	$(".da_tfo").hover(function() {
		$(this).addClass("data_cfrist_active");
		$(this).removeClass("data_cfrist");
		showtype(1, this);
	}, function() {
		$(this).addClass("data_cfrist");
		$(this).removeClass("data_cfrist_active");
		showtype(0, this);
	});
	$(".da_tfot").hover(function() {
		$(this).addClass("data_csencod_active");
		$(this).removeClass("data_csencod");
		showtype(1, this);
	}, function() {
		$(this).addClass("data_csencod");
		$(this).removeClass("data_csencod_active");
		showtype(0, this);
	});
	$(".da_tfos").hover(function() {
		$(this).addClass("data_cthree_active");
		$(this).removeClass("data_cthree");
		showtype(1, this);
	}, function() {
		$(this).addClass("data_cthree");
		$(this).removeClass("data_cthree_active");
		showtype(0, this);
	});

}

function showtype(type, obj) {
	if (type == 1) {
		$(obj).find(".data_cf_one").hide();
		$(obj).find(".data_cf_two").show();
	} else {
		$(obj).find(".data_cf_one").show();
		$(obj).find(".data_cf_two").hide();
	}
}

function ScreenWidth() {
	var height = $(document).height(); 
	if (height<= 799 && height>=783) { 
		$("#h_khd").attr("rel", "300,980,181,99"); 
		$("#h_shd").attr("rel", "1000,-3500,814,99"); 
	}
	else if (height == 3511 ) { 
		$("#h_khd").attr("rel", "300,980,176,98"); 
		$("#h_shd").attr("rel", "1000,-3500,814,98"); 
	} 
	else if (height==775) { 
		$("#h_khd").attr("rel", "300,980,140,99"); 
		$("#h_shd").attr("rel", "1000,-3500,784,99"); 
	}
}